INSERT INTO aml_susmodel (susModelCode, susModelName, enable, RICD, RPNC, RITP, DETR, DORP, ODRP, TPTR, OTPR, STCB, AOSP, des, featureCodes) VALUES ('1103', '长期闲置账户近期大量资金频繁转入转出', '1', 'C5008131000000', 'C5008131000000', 'J003', '01', '02', '', '01', '', '', '', '长期闲置的账户，在近期内频繁发生资金的转入转出.说明：长期闲置账户：一个时期范围内，账户交易笔数小于一定值，交易金额小于一定值近期频繁转入转出：短期内，转入转出金额大于一定值例如：账户A在365(10天前的365天内)内交易金额小于5000，交易笔数小于等于5笔，被认为是长期闲置账户，在最近10天(短期)内，突然转入转出金额大于200万，且交易笔数大于等于5笔，被认为是短期频繁转入转出', '80201,80202,80203,80204');
